refentry.refsect1:
  &reftitle.description;
  
   public mysql_xdevapi\SqlStatementmysql_xdevapi\Session::sql
   stringquery
  


   Create a native SQL command. Placeholders are supported using the native "?" syntax.
   Execute an SQL query.
  

  &warn.undocumented.func;

  &reftitle.examples;
  
   mysql_xdevapi\Session::sql example
   

<?php

$res = $session->sql("insert into test_db.test_table values ('Carlos',9999)")->execute();
$res = $session->sql("select age, age/2 as x from test_db.test_table limit 2")->execute();
$res = $session->sql("select * from $db.test_table where age < ? order by age desc limit ?")->bind(14)->bind(2)->execute();

?>
