chapter:
 &reftitle.examples;



  Lexer examples

   Tokenize assign statement


<?php

use Parle\{Token, Lexer};

$lex = new Lexer;

$lex->push("\$[a-zA-Z_][a-zA-Z0-9_]*", 1);
$lex->push("=", 2);
$lex->push("\d+", 3);
$lex->push(";", 4);

$lex->build();

$lex->consume('$x = 42; $y = 24;');


do {
	$lex->advance();
	$tok = $lex->getToken();
	var_dump($tok);
} while (Token::EOI != $tok->id);
