 &reftitle.examples;



  Lexer examples

   Tokenize comma separated integer list


<?php

use Parle\Token;
use Parle\Lexer;
use Parle\LexerException;

/* name => id */
$token = array(
        "EOI" => 0,
        "COMMA" => 1,
        "CRLF" => 2,
        "DECIMAL" => 3,
);
/* id => name */
$token_rev = array_flip($token);

$lex = new Lexer;
$lex->push("[\x2c]", $token["COMMA"]);
$lex->push("[\r][\n]", $token["CRLF"]);
$lex->push("[\d]+", $token["DECIMAL"]);
$lex->build();

$in = "0,1,2\r\n3,42,5\r\n6,77,8\r\n";

$lex->consume($in);

do {
        $lex->advance();
        $tok = $lex->getToken();

        if (Token::UNKNOWN == $tok->id) {
                throw new LexerException("Unknown token '{$tok->value}' at offset {$tok->offset}.");
        }

        echo "TOKEN: ", $token_rev[$tok->id], PHP_EOL;
} while (Token::EOI != $tok->id);




   Tokenize assign statement


<?php

use Parle\{Token, Lexer};

$lex = new Lexer;

$lex->push("\$[a-zA-Z_][a-zA-Z0-9_]*", 1);
$lex->push("=", 2);
$lex->push("\d+", 3);
$lex->push(";", 4);

$lex->build();

$lex->consume('$x = 42; $y = 24;');


do {
	$lex->advance();
	$tok = $lex->getToken();
	var_dump($tok);
} while (Token::EOI != $tok->id);






  Parser examples

   Parse words out from a sentense


<?php

use Parle\{Lexer, Token, Parser, ParserException};

$p = new Parser;
$p->token("WORD");
$p->push("START", "SENTENCE");
$p->push("SENTENCE", "WORDS");
$words_idx = $p->push("WORDS", "WORDS WORD");
$word_idx = $p->push("WORDS", "WORD");
$p->build();

$lex = new Lexer;
$lex->push("[^\s]{-}[\.,\:\;\?]+", $p->tokenId("WORD"));
$lex->push("[\s\.,\:\;\?]+", Token::SKIP);
$lex->build();

$in = "Dis-moi oÃ¹ est ton papa?";
$p->consume($in, $lex);
do {
	$act = $p->action();
	switch ($act) {
	case Parser::ACTION_ERROR:
		throw new ParserException("Error");
		break;
	case Parser::ACTION_SHIFT:
	case Parser::ACTION_GOTO:
	case Parser::ACTION_ACCEPT:
		/* var_dump($p->trace());*/
		break;
	case Parser::ACTION_REDUCE:
		$rid = $p->reduceId();
		if ($rid == $word_idx) {
			var_dump($p->sigil(0));
		} if ($rid == $words_idx) {
			var_dump($p->sigil(1));
		}
		break;
	}
	$p->advance();
} while (Parser::ACTION_ERROR != $act && Parser::ACTION_ACCEPT != $act);
